const MAX_DESCRIPTION_LENGTH = 100;

class HotelCard extends React.Component {
    render() {
      return (<div className="hotrl-kard">
                <img className="hotel-image" src={this.props.img} alt="zakr"/>
                    <div className="hotel-body">
                        <h1 className="hotel-title">{this.props.name} </h1>
                        <h4 className="hotel-desc">
                            {
                                this.props.desc.length > MAX_DESCRIPTION_LENGTH
                                ? this.props.desc.substring(0,MAX_DESCRIPTION_LENGTH) + "..."
                                : this.props.desc
                            }
                        </h4>
                    </div>

                    <div className="hotel-book">
                        <span className="hotel-price">{this.props.price}$</span>
                        <a href={`https://www.hotels24.ua`}
                           className="hotel-book-link"
                           target="_blank"
                        >Book</a>
                    </div>    
            </div>
      )
    }
  }

  class SearchBar extends React.Component {
    render() {
      return (<div className="search-bar">
                <input type="text"
                className="search-input"
                 placeholder="Look for..."
                 onChange={this.props.onSearch}/>
            </div>      
            )
    }
  }

  class HotelApp extends React.Component {
      constructor(props) {
          super(props);
          this.state = {displayedHotels: ROOMS};

          this.handleSearch = this.handleSearch.bind(this);
      }

    handleSearch(event){
          const search = event.target.value.toLowerCase();

          const displayedHotels= ROOMS.filter(hotel =>{
              const searchString =hotel.name.toLowerCase() + hotel.description.toLowerCase();

              return searchString.indexOf(search) !== -1;
          });

      this.setState({
          displayedHotels
      });

    }

    render() {
        const hotelCard = this.state.displayedHotels.map(hotel =>
            <HotelCard
               key={hotel.id}
               name={hotel.name } 
               desc={hotel.description}
               price={hotel.price}
               img={hotel.image}          
            />
        );

        return (
            <div className="app">
                <div className="header">Hotel look</div>
                    <SearchBar onSearch={this.handleSearch}/>
                <div className="hotel-list">
                    {hotelCard}
                </div>
            </div>
        );
    }
  }


ReactDOM.render(
    <HotelApp/>,
    document.getElementById("root")
)